curl -XPOST "http://localhost:9200/insurance_products/_create/4" -H 'Content-Type: application/json' -d'
{
  "product_name": "多倍领航重大疾病保险（赢家版）",
  "insurance_type": "医疗保险",
  "age_limit": "18周岁至60周岁",
